var sleepTimeout;

var current_track_id = 0;
var flashPlayerActivo = 0;
var temazoTortazoActivo = 0;

function audioInit() {
    var mp3 = 'data:audio/mpeg;base64,/+MYxAAAAANIAUAAAASEEB/jwOFM/0MM/90b/+RhST//w4NFwOjf///PZu////9lns5GFDv//l9GlUIEEIAAAgIg8Ir/JGq3/+MYxDsLIj5QMYcoAP0dv9HIjUcH//yYSg+CIbkGP//8w0bLVjUP///3Z0x5QCAv/yLjwtGKTEFNRTMuOTeqqqqqqqqqqqqq/+MYxEkNmdJkUYc4AKqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqq';

    var ogg = 'data:audio/ogg;base64,';

    try {
        var audio = new Audio();
        var src = audio.canPlayType('audio/ogg') ? ogg : mp3;
        audio.autoplay = true;
        audio.volume = 0;

        // this will only be triggered if autoplay works
        $(audio).on('play', function() {
            $('#player_main_ctrl').addClass('player_pause');
        });

        audio.src = src;
    } catch(e) {
        console.log('[AUTOPLAY-ERROR]', e);
        $('#player_main_ctrl').addClass('player_play');
    }
}

function aud_play_pause() {
    if (audioStream.paused) {
        $('#player_main_ctrl').removeClass('player_play');
        $('#player_main_ctrl').addClass('player_pause');
        $('#player_spinner').removeClass('pause');
        audioStream.play();
    } else {
        $('#player_main_ctrl').removeClass('player_pause');
        $('#player_main_ctrl').addClass('player_play');
        $('#player_spinner').addClass('pause');
        audioStream.pause();
    }
}

function stopAudio() {
    audioStream.pause(0);
    audioStream.src = "";
    audioStream.load();
    $("audioStream").remove();
}

function loadFlashPlayer() {
    if (flashPlayerActivo == 0) {
        $("#flashPlayer").html("<embed type=\"application/x-shockwave-flash\" src=\"static/audio/player2.swf\" height=\"24\" style=\"undefined\" id=\"playerFlash\" name=\"player\" bgcolor=\"#000000\" quality=\"high\" allowscriptaccess=\"always\" allowfullscreen=\"false\" flashvars=\"file=http://91.121.68.52:8012/;stream.nsv&amp;provider=sound&amp;bufferlength=2&amp;autostart=true\" wmode=\"opaque\">");
        //$("#flashPlayer").html("<embed type=\"application/x-shockwave-flash\" src=\"http://www.todostreaming.es/player2.swf\" height=\"24\" style=\"undefined\" id=\"playerFlash\" name=\"player\" bgcolor=\"#000000\" quality=\"high\" allowscriptaccess=\"always\" allowfullscreen=\"false\" flashvars=\"file=http://195.55.74.208/cope/megastar.mp3&amp;autostart=true\" wmode=\"opaque\">");
        $('.player_circle').remove();
        stopAudio();
        flashPlayerActivo = 1;
    } else {
        null;
    }
}

function checkPlayer() {
    var audioStream = document.getElementById("stream");
    if (flashPlayerActivo == 0) {
        if (audioStream.playing) {
            $('#player_main_ctrl').removeClass('player_pause');
            $('#player_main_ctrl').addClass('player_play');
            $('#player_spinner').addClass('pause');
        } else {
            $('#player_main_ctrl').removeClass('player_play');
            $('#player_main_ctrl').addClass('player_pause');
            $('#player_spinner').removeClass('pause');
        }
    }
}

function indicativo() {
    if (flashPlayerActivo == 0) {
        $( "#logo" ).mouseover(function() {
            audioStream.volume = 0.2;
            audioIndicativo.play();
            setTimeout(function() {
                audioStream.volume = 1.0;
            }, 2700);
        });
    }
}
